[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<div class="print-heading">				<h2><div>Purchase Order</div><br><small class="sub-heading">{{ doc.name }}</small>				</h2></div>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "supplier_name", "print_hide": 0, "align": "left", "label": "Supplier"}, {"fieldname": "supplier_address_display", "print_hide": 0, "align": "left", "label": "Supplier Address"}, {"fieldname": "default_delivery_location", "print_hide": 0, "label": "Default Delivery Location"}, {"fieldtype": "Column Break"}, {"fieldname": "po_date", "print_hide": 0, "label": "PO Date"}, {"fieldname": "comments", "print_hide": 0, "label": "Comments"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "{% set item_details = json.loads(doc.item_details) %}
<table class="table table-sm table-bordered">
    {% for i in item_details %}
    <tr>
        <td>
            <div class="row">
                <div class="col-md-4">
                    <span class="bold"> Item: </span>{{ i.item }}
                </div>
                <div class="col-md-4">
                    <span class="bold"> Delivery Location: </span>{{ i.delivery_location }}
                </div>
                <div class="col-md-4">
                    <span class="bold"> Expected Delivery Date: </span>{{ i.delivery_date }}
                </div>
            </div>
            <table class="table table-sm table-bordered">
                <tr>
                    <th>S.No.</th>
                    {% for attr in i.variants[0].attributes %}
                    <th>{{ attr }}</th>
                    {% endfor %}
                    {% for attr in i.variants[0]['values'] %}
                        {% if attr == 'default' %}
                        <th>Quantity</th>
                        {% else %}
                        <th>{{ attr }}</th>
                        {% endif %}
                    {% endfor %}
                </tr>
                {% for variant in i.variants %}
                <tr>
                    <td>{{ loop.index }}</td>
                    {% for attr in variant.attributes %}
                    <td>{{ variant.attributes[attr] }}</td>
                    {% endfor %}
                    {% for attr in variant['values'] %}
                    <td>
                        <div>
                            {{ variant['values'][attr].qty }}{{ ' ' + i.default_uom}}
                            {% if variant['values'][attr].secondary_qty %}
                            <span>
                                <br>
                                {{ variant['values'][attr].secondary_qty }}{{ ' ' + i.secondary_uom}}
                            </span>
                            {% endif %}
                            <br>
                            Rate: {{ variant['values'][attr].rate }}
                        </div>
                    </td>
                    {% endfor %}
                </tr>
                {% endfor %}

            </table>
        </td>
    </tr>
    {% endfor %}
</table>"}]
